
public class SwapNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int x=10;
		int y=20;
		
		System.out.println("Before swaping x=" + x + "and y = " +y);
		x=x+y; //30
		y=x-y;  //10//30-20
		x=x-y;  //30-10=20
		System.out.println("After swaping x=" + x + "and y = " +y );

	}

}
